// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router';
import Home from '../views/Home.vue'; // (Home.vue 也会有这个提示，最好也改成 HomeView.vue)

// 更新 import 路径
import LoginView from '../views/LoginView.vue'; 
import SignupView from '../views/SignupView.vue';

import ProductList from '../views/ProductList.vue';
import ProductOrder from '../views/ProductOrder.vue';

const routes = [
  { path: '/', name: 'Home', component: Home }, 

  // 更新 name 和 component
  { path: '/login', name: 'LoginView', component: LoginView },
  { path: '/signup', name: 'SignupView', component: SignupView },

  { path: '/products', name: 'Products', component: ProductList },
  { path: '/create-order', name: 'CreateOrder', component: ProductOrder }
];

// ... (router 创建和导出的其余代码不变)
export default router;
