import { ref } from 'vue';
import { apiFetch, login } from '@/services/apiService.js';
import { useAuth } from '@/store/auth.js';

// 这一行在 <script setup> 中是绝对合法的

export default {
  __name: 'AuthView',
  emits: ['response'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const emit = __emit;
    const {
      saveToken
    } = useAuth();
    const signupData = ref({
      username: '',
      password: '',
      role: '',
      phone: ''
    });
    const loginData = ref({
      username: '',
      password: ''
    });
    async function handleSignup() {
      try {
        //
        const result = await apiFetch('/signup', {
          method: 'POST',
          body: JSON.stringify(signupData.value)
        });
        emit('response', result); //
        signupData.value = {
          username: '',
          password: '',
          role: '',
          phone: ''
        };
      } catch (error) {
        emit('response', {
          code: 0,
          msg: error.message,
          data: null
        }); //
      }
    }
    async function handleLogin() {
      try {
        //
        const result = await login(loginData.value.username, loginData.value.password);
        saveToken(result.data); //

        emit('response', result);
        loginData.value = {
          username: '',
          password: ''
        };
      } catch (error) {
        emit('response', {
          code: 0,
          msg: error.message,
          data: null
        }); //
      }
    }
    const __returned__ = {
      emit,
      saveToken,
      signupData,
      loginData,
      handleSignup,
      handleLogin,
      ref,
      get apiFetch() {
        return apiFetch;
      },
      get login() {
        return login;
      },
      get useAuth() {
        return useAuth;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
