import { ref } from 'vue';
import { apiFetch } from '@/services/apiService.js';

// 1. 定义 emit 函数

export default {
  __name: 'OrderView',
  emits: ['response'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const emit = __emit;

    // 2. 创建订单表单的响应式数据
    const orderData = ref({
      productId: null,
      buyerId: null,
      status: ''
    });

    // 3. 处理创建订单
    async function handleCreateOrder() {
      try {
        //
        const result = await apiFetch('/product-orders', {
          method: 'POST',
          body: JSON.stringify(orderData.value)
        });
        emit('response', result); //
        // 清空表单
        orderData.value = {
          productId: null,
          buyerId: null,
          status: ''
        };
      } catch (error) {
        emit('response', {
          code: 0,
          msg: error.message,
          data: null
        }); //
      }
    }
    const __returned__ = {
      emit,
      orderData,
      handleCreateOrder,
      ref,
      get apiFetch() {
        return apiFetch;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
